var ez = require("../"),
client = new ez.Client('http://127.0.0.1:' + 0xFFFA + '/');

console.log("\t(2) Connecting to 127.0.0.1 / " + 0xFFFA + ".");

client.subscribe('/messages', function(message) {
	console.log("\t(2) Got a message: '" + message.text + "'", "from " + "(3)");
	console.log("\nExiting.");
	process.exit();
});

console.log("\t(2) Subscribed to /messages");
